class DataParser:
    def __init__(self):
        self.target_renamed = 'Porosity'
        self.target = 'porosity'
        self.ratios = [0.8]
        self.seed = 42
        # 19 columns. All chosen columns that might be relevant for porosity prediction.
        self.all_cols = ['name_part1', 'material', 'name_part2',
                         'name_fluid1', 'technique', 'direction',  # 'sublimated'
                         'material_group',
                         'name_mold_mat',
                         'name_disp_1', 'wf_disp_1',
                         'name_bind1', 'wf_bind_1',
                         'temp_cold', 'cooling_rate',
                         'time_sub',
                         'time_sinter_1', 'temp_sinter_1', 'vf_total', 'porosity']
        # 11 columns
        self.reduced_cols = ['name_part1', 'name_part2',
                             'name_fluid1',  # 'sublimated', 'technique', 'direction',
                             'material_group',
                             'temp_cold', 'cooling_rate',
                             'time_sub',
                             'time_sinter_1', 'temp_sinter_1', 'vf_total', 'porosity']
        # 16 columns
        self.mid_cols = ['name_part1', 'name_part2',
                         'name_fluid1',  # 'sublimated', 'technique', 'direction',
                         'material_group',
                         'name_mold_mat',
                         'name_disp_1', 'wf_disp_1',
                         'name_bind1', 'wf_bind_1',
                         'temp_cold', 'cooling_rate',
                         'time_sub',
                         'time_sinter_1', 'temp_sinter_1', 'vf_total', 'porosity']

        # Selected Cols:
        self.col_dtypes = {'material': 'enum', 'name_part1': 'enum', 'name_part2': 'enum', 'name_fluid1': 'enum',
                           'technique': 'enum', 'direction': 'enum', 'sublimated': 'enum',
                           'name_mold_mat': 'enum',
                           'name_disp_1': 'enum', 'wf_disp_1': 'numeric', 'name_bind1': 'enum', 'wf_bind_1': 'numeric',
                           'material_group': 'enum', 'temp_cold': 'numeric', 'cooling_rate': 'numeric',
                           'time_sub': 'numeric',
                           'time_sinter_1': 'numeric', 'temp_sinter_1': 'numeric', 'vf_total': 'numeric',
                           'porosity': 'numeric'}

        self.col_dtypes_renamed = {'Material Name': 'enum', 'Solid Name': 'enum', 'Solid Name 2': 'enum', 'Fluid Name': 'enum',
                                   'Mold Material Name': 'enum',
                                   'Disp. Name': 'enum', 'Disp. wf.': 'numeric', 'Binder Name': 'enum',
                                   'Binder wf.': 'numeric',
                                   'Group': 'enum', 'Temp. Cold': 'numeric', 'Cooling Rate': 'numeric',
                                   'Time Sub.': 'numeric',
                                   'Time Sinter.': 'numeric', 'Temp Sinter': 'numeric', 'Solid Loading': 'numeric',
                                   'Porosity': 'numeric'}

        self.col_rename = {'material': 'Material', 'name_part1': 'Solid Name', 'name_part2': 'Solid Name 2', 'name_fluid1': 'Fluid Name',
                           'name_mold_mat': 'Mold Material Name',
                           'name_disp_1': 'Disp. Name', 'wf_disp_1': 'Disp. wf.', 'name_bind1': 'Binder Name',
                           'wf_bind_1': 'Binder wf.',
                           'material_group': 'Group', 'temp_cold': 'Temp. Cold', 'cooling_rate': 'Cooling Rate',
                           'time_sub': 'Time Sub.',
                           'time_sinter_1': 'Time Sinter.', 'temp_sinter_1': 'Temp Sinter', 'vf_total': 'Solid Loading',
                           'porosity': 'Porosity'}
        self.all_cat_cols = [key for key in self.col_dtypes if self.col_dtypes[key] == 'enum']
        self.all_num_cols = [key for key in self.col_dtypes if self.col_dtypes[key] != 'enum']
        self.feats_dict = {'all_cols': self.all_cols, 'mid_cols': self.mid_cols, 'reduced_cols': self.reduced_cols}

        # technique', 'sublimated' only have one value / direction' only has 600 rows

    def load_complete_data_from_mysql(self):
        df = db_to_df().copy()
        df['vf_solid'] = df['vf_part_1'] / (df['vf_part_1'] + df['vf_fluid_1'])  # wrong values, use vf_total
        df = df.fillna(value=np.nan)
        return df

    def load_complete_data_from_pickle(self, filepath='data/freeze_casting_df_complete.pkl'):
        df = pd.read_pickle(filepath)
        return df

    def save_df_to_pickle(self, pkl_filename, df):
        with open(pkl_filename, 'wb') as file:
            pickle.dump(df, file)

    def preprocess_dropna(self, df):
        # Drop samples with no porosity values
        df = df[df['porosity'].notna()]
        return df

    def remove_vf_above_1(self, df):
        return df[df['vf_total'] <= 1]

    def preprocess_drop_not_sublimated(self, df):
        # Drop samples with no porosity values
        df = df[df['sublimated'] != 'N']
        return df

    def rename_columns_df(self, df):
        return df.rename(columns=self.col_rename)

    def rename_columns_h2o(self, h2o_data):
        return h2o_data.rename(self.col_rename)

    def load_pipeline(self, model_path):
        import joblib
        with open(model_path, 'rb') as f:
            pipeline = joblib.load(model_path)
        return pipeline


if __name__ == '__main__':
    parser = DataParser()

    """Use MySQL database to create .pkl file"""
    # df = data_parser.load_complete_data_from_mysql()
    # data_parser.save_df_to_pickle('freeze_casting_df_complete.pkl', df)
    # print(df['direction'].value_counts(dropna=False))

    """Load df from saved .pickle"""
    df_complete = parser.load_complete_data_from_pickle('data/freeze_casting_df_complete.pkl')
    # save complete dataframe
    df_complete.to_csv('data/freeze_casting_df_complete.csv')

    # rename before save
    # df = data_parser.rename_columns_df(df)
    # train = data_parser.rename_colum-
    # ns_df(train)
    # test = data_parser.rename_columns_df(test)

    # save frames
    for key in parser.feats_dict.keys():
        feats = parser.feats_dict[key]
        df = parser.preprocess_drop_not_sublimated(df_complete)
        df = df[feats]
        df = parser.preprocess_dropna(df)
        df = parser.remove_vf_above_1(df)
        X_train, X_test, y_train, y_test = train_test_split(df[feats], df['porosity'],
                                                            test_size=1 - parser.ratios[0],
                                                            random_state=parser.seed)
        train = X_train.copy()
        train['porosity'] = y_train
        test = X_test.copy()
        test['porosity'] = y_test

        df_dir = f'data/{key}'
        if not os.path.isdir(df_dir):
            os.makedirs(df_dir)
        # df_complete.to_csv(f'{df_dir}/df_complete.csv', index=False)
        df.to_csv(f'{df_dir}/df_selected_feats.csv', index=False)
        train.to_csv(f'{df_dir}/train.csv', index=False)
        test.to_csv(f'{df_dir}/test.csv', index=False)

    # plot vf_total vs porosity
    # plot vf_total vs porosity
    fig, ax = plt.subplots(figsize=(8, 10))  # Adjust width and height as needed
    scatter = sns.scatterplot(x=df['vf_total'], y=df['porosity'], hue=df['material_group'])
    legend = scatter.legend(loc='upper right')  # Adjust legend location if needed
    legend.get_frame().set_facecolor('lightgrey')
    plt.show()

    print("saved frames")
